mapper.select:
SELECT useq,id,name,email,regdate,blacklist
	FROM
		(												
		SELECT row_number() over(ORDER BY regdate DESC) AS un,useq,id,name,email,regdate,blacklist
			FROM users

				WHERE id LIKE '%'||#{id}||'%'


		)

		WHERE un<=#{criteria.pageNum} * #{criteria.rowsPerPage}  
			AND un>=(#{criteria.pageNum}-1) * #{criteria.rowsPerPage}
			ORDER BY useq DESC
