mapper.select:


SELECT oseq, camp_name, camp_zone, user_id, order_name, indate, outdate, total_price, order_phone, order_email
	FROM
		(
		SELECT row_number() over(ORDER BY indate DESC) rn, oseq, camp_name, camp_zone, user_id, order_name, indate, outdate, total_price, order_phone, order_email
			FROM camp_order 
				WHERE camp_name=#{camp_name}	
		)
		WHERE rn<=#{criteria.pageNum} * #{criteria.rowsPerPage}  
			AND rn>=(#{criteria.pageNum}-1) * #{criteria.rowsPerPage}
